CREATE DATABASE seoul_eats;

-- 'seoul_eats' 데이터베이스를 사용합니다.
USE seoul_eats;

-- 'seoul_restaurants' 테이블을 생성합니다.
CREATE TABLE `seoul_restaurants` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `district` VARCHAR(20) NOT NULL,
  `restaurant_name` VARCHAR(100) NULL,
  `rating` DECIMAL(2,1) NULL,
  `food_type` VARCHAR(50) NULL,
  `address` VARCHAR(255) NOT NULL,
  `latitude` DECIMAL(10,8) NOT NULL,
  `longitude` DECIMAL(11,8) NOT NULL,
  `nearby_station` VARCHAR(100) NULL,
  `opening_hours` VARCHAR(100) NULL,
  `tags` VARCHAR(255) NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

